<template>
	<view class="container">
		<!-- 小程序头部兼容 -->
		<view class="mp-search-box">
				<uni-search-bar placeholder="请输入搜素关键字"  bgColor="rgba(227, 227, 227, 0.8)"
				 cancelText="搜索"
				 @cancel="search()" />
		
		</view>
		
		<!-- 头部轮播 -->
		<view class="carousel-section">
			<!-- 标题栏和状态栏占位符 -->
			<view class="titleNview-placing"></view>
			<!-- 背景色区域 -->
			<view class="titleNview-background" :style="{backgroundColor:titleNViewBackground}"></view>
			<swiper class="carousel" circular @change="swiperChange">
				<swiper-item v-for="(item, index) in carouselList" :key="index" class="carousel-item" >
					<image :src="item" />
				</swiper-item>
			</swiper>
			<!-- 自定义swiper指示器 -->
			<view class="swiper-dots">
				<text class="num">{{swiperCurrent+1}}</text>
				<text class="sign">/</text>
				<text class="num">{{swiperLength}}</text>
			</view>
		</view>
		<!-- 分类 -->
		<classify></classify>
	
		
		<!-- 秒杀楼层 -->
		<seckilll :goodsList="seckillgoods"></seckilll>
		
		
		<!-- 分类推荐楼层 -->
		<recommend :goodsList="goodsList"></recommend>
	
		
	<othervue :goodsList="goodsList"></othervue>
		

	</view>
</template>

<script>
import othervue from './other.vue'
import classify from './classify.vue'
import seckilll from "./seckill.vue"
import recommend from './recommend.vue'
	export default {
		components:{
			othervue,classify,seckilll,recommend
		},
		data() {
			return {
				titleNViewBackground: '',
				swiperCurrent: 0,
				swiperLength: 0,
				
				searchvalue:"",
				//轮播图数据
				carouselList: [],
				//商品
				goodsList: this.$store.state.goodsList,
				//秒杀商品
				seckillgoods:[]
			};
		},

		mounted() {
			this.loadData();
		},
		methods: {
			/**
			 * 请求静态数据只是为了代码不那么乱
			 * 分次请求未作整合
			 */
			async loadData() {
				let that =this
				await this.$myRequest({
					url:"/carouselList",
					method:"GET"
				}).then((res)=>{
						console.log(res.data.data)
						that.carouselList =res.data.data
						console.log(that.carouselList)
						let background="rgb(203, 87, 60)"
						that.titleNViewBackground = background;
						that.swiperLength = that.carouselList.length;
				})
				await this.$myRequest({
					url:"/goodsList",
					method:"GET"
				}).then((res)=>{
					console.log(res.data.data)
				
					that.$store.state.goodsList=res.data.data
					that.goodsList=that.$store.state.goodsList
					that.seckillgoods=res.data.data
				})
				
				// let goodsList = await this.$api.json('goodsList');
				// this.goodsList = goodsList || [];
				// this.seckillgoods= goodsList || [];
			},
			//轮播图切换修改背景色
			swiperChange(e) {
				const index = e.detail.current;
				this.swiperCurrent = index;
			},
		search(e){
			let search=e.value
			uni.redirectTo({
				url:`/pages/product/list?search=${search}`
			})
		}
		},
		
	}
</script>

